sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDatePast: function(strDate) {
		var rightnow = new GlideDateTime();
		var testDate = new GlideDateTime(strDate);

		if (testDate.before(rightnow))
		{
			return true;
		}
		else 
		{
			return false;
		}
	},

	isDateToday: function(strDate)
	{
		var rightNow = new GlideDateTime();
		rightNow = rightNow.getDate();

		var neededDate = new GlideDateTime(strDate);
		neededDate = neededDate.getDate();

		if (rightNow.equals(neededDate))
		{
			return true;
		}
		return false;
	},

	//Ignore: this is an exercise you made for yourself.
	greetUser: function(bool)
	{
		if(bool)
		{
			return "Hello person!";
		}
	},

    type: 'NeedItUtils'
};
